import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const EditJobPage = () => {
  const [formData, setFormData] = useState(null);
  const [loading, setLoading] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();

  const loadJob = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(`/api/jobs/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFormData(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to load job");
    }
  };

  useEffect(() => {
    loadJob();
  }, [id]);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      await axios.put(`/api/jobs/${id}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      navigate("/company/posted-jobs");
    } catch (err) {
      console.error(err);
      alert("Update failed");
    } finally {
      setLoading(false);
    }
  };

  if (!formData) return <div className="p-6">Loading...</div>;

  return (
    <div className="flex min-h-screen bg-slate-100">
      {/* sidebar */}

      <div className="flex-1 p-6">
        <form
          onSubmit={handleSubmit}
          className="bg-white rounded-md shadow p-6 space-y-4 max-w-4xl mx-auto"
        >
          <div className="flex justify-between items-center mb-2">
            <h2 className="text-lg font-semibold">Edit Job</h2>
            <div className="space-x-2">
              <button
                type="button"
                onClick={() => navigate("/company/posted-jobs")}
                className="bg-red-600 text-white px-4 py-1 rounded"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading}
                className="bg-green-600 text-white px-4 py-1 rounded disabled:opacity-60"
              >
                {loading ? "Saving..." : "Save"}
              </button>
            </div>
          </div>

          {/* reuse same inputs as AddJobPage, bound to formData */}
          {/* For brevity only first few shown; copy the same structure as AddJobPage */}

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-semibold mb-1">
                Job title
              </label>
              <input
                name="title"
                value={formData.title}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            {/* ... repeat all other inputs / textareas using formData + handleChange ... */}
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditJobPage;
