// src/pages/EditJobPage.js
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const emptyState = {
  title: "",
  category: "",
  department: "",
  studentCategory: "",
  gender: "Any",
  deadline: "",
  address: "",
  description: "",
  requirements: "",
  benefits: "",
  experience: "",
  salaryRange: "",
};

const EditJobPage = () => {
  const [formData, setFormData] = useState(emptyState);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const loadJob = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`/api/jobs/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFormData({
          title: res.data.title || "",
          category: res.data.category || "",
          department: res.data.department || "",
          studentCategory: res.data.studentCategory || "",
          gender: res.data.gender || "Any",
          deadline: res.data.deadline?.slice(0, 10) || "",
          address: res.data.address || "",
          description: res.data.description || "",
          requirements: res.data.requirements || "",
          benefits: res.data.benefits || "",
          experience: res.data.experience || "",
          salaryRange: res.data.salaryRange || "",
        });
      } catch (err) {
        console.error(err);
        alert(err.response?.data?.error || "Failed to load job");
        navigate("/company/posted-jobs");
      } finally {
        setLoading(false);
      }
    };
    loadJob();
  }, [id, navigate]);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setSaving(true);
      const token = localStorage.getItem("token");
      await axios.put(`/api/jobs/${id}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      navigate("/company/posted-jobs");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Failed to update job");
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => navigate("/company/posted-jobs");

  if (loading) return <div className="p-6">Loading...</div>;

  return (
    <div className="flex min-h-screen bg-slate-100">
      <div className="flex-1 p-6">
        <form
          onSubmit={handleSubmit}
          className="bg-white rounded-md shadow p-6 space-y-4 max-w-4xl mx-auto"
        >
          <div className="flex justify-between items-center mb-2">
            <h2 className="text-lg font-semibold">Edit Job</h2>
            <div className="space-x-2">
              <button
                type="button"
                onClick={handleCancel}
                className="bg-red-600 text-white px-4 py-1 rounded"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={saving}
                className="bg-green-600 text-white px-4 py-1 rounded disabled:opacity-60"
              >
                {saving ? "Saving..." : "Save"}
              </button>
            </div>
          </div>

          {/* same fields as AddJobPage */}
          {/* reuse exactly the same JSX for inputs/textareas */}
          {/* for brevity, copy the blocks from AddJobPage and just use formData + handleChange */}
          {/* ... (copy the grid and textarea section from AddJobPage here unchanged) */}
        </form>
      </div>
    </div>
  );
};

export default EditJobPage;
