// src/pages/PostedJobsPage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const PostedJobsPage = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const fetchJobs = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/jobs/company", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobs(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert(
        err.response?.data?.error ||
          err.response?.data?.message ||
          "Failed to load jobs"
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this job?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/jobs/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobs((prev) => prev.filter((job) => job._id !== id));
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert(
        err.response?.data?.error ||
          err.response?.data?.message ||
          "Failed to delete job"
      );
    }
  };

  return (
    <div className="flex min-h-screen bg-slate-100">
      <div className="flex-1 p-6">
        <h2 className="text-xl font-semibold mb-4">Posted Jobs</h2>

        {loading ? (
          <p>Loading...</p>
        ) : jobs.length === 0 ? (
          <p>No jobs posted yet.</p>
        ) : (
          <div className="space-y-4">
            {jobs.map((job) => (
              <div
                key={job._id}
                className="bg-white shadow rounded-md p-4 flex justify-between items-start"
              >
                <div className="text-sm">
                  <p>
                    <span className="font-semibold">Job Title:</span>{" "}
                    {job.title}
                  </p>
                  <p>
                    <span className="font-semibold">Job Category:</span>{" "}
                    {job.category}
                  </p>
                  <p>
                    <span className="font-semibold">Department:</span>{" "}
                    {job.department}
                  </p>
                  <p>
                    <span className="font-semibold">Student Category:</span>{" "}
                    {job.studentCategory}
                    {"  "}
                    <span className="font-semibold ml-4">Gender:</span>{" "}
                    {job.gender}
                  </p>
                  <p>
                    <span className="font-semibold">Deadline:</span>{" "}
                    {job.deadline}
                  </p>
                  <p>
                    <span className="font-semibold">Address:</span>{" "}
                    {job.address}
                  </p>
                  <p className="font-semibold mt-2">Job Description</p>
                  <p className="whitespace-pre-line text-gray-700">
                    {job.description}
                  </p>
                </div>

                <div className="space-y-2 ml-4">
                  <button
                    className="bg-green-600 text-white px-4 py-1 rounded"
                    onClick={() => navigate(`/company/jobs/${job._id}/edit`)}
                  >
                    Edit
                  </button>
                  <button
                    className="bg-red-600 text-white px-4 py-1 rounded"
                    onClick={() => handleDelete(job._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PostedJobsPage;
