import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const API_BASE = "http://localhost:5000/api/auth";

const UserRegisterPage = () => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    gender: "",
    email: "",
    mobile: "",
    password: "",
    confirmPassword: "",
    studentType: "",
    department: "",
  });

  const [imageFile, setImageFile] = useState(null);


  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImageFile(file);
    setImagePreview(URL.createObjectURL(file)); // simple preview only [web:108][web:117]
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // stop page reload [web:107][web:116]
    setMessage("");

    if (form.password !== form.confirmPassword) {
      setMessage("Passwords do not match");
      return;
    }

    try {
      setLoading(true);

      // for now, send only text fields; image upload can be added later with Cloudinary
      const body = {
        name: form.name,
        gender: form.gender,
        email: form.email,
        mobile: form.mobile,
        password: form.password,
        studentType: form.studentType,
        department: form.department,
      };

      const res = await fetch(`${API_BASE}/register-user`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      }); // standard JSON POST from React to Express [web:102][web:106]

      const data = await res.json();

      if (!res.ok) {
        setMessage(data.message || "Registration failed");
        setLoading(false);
        return;
      }

      // success – you could store token, then navigate
      // localStorage.setItem("token", data.token);
      setMessage("Account created successfully");
      setLoading(false);
      navigate("/login");
    } catch (err) {
      console.error(err);
      setMessage("Server error");
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-sky-100">
      <header className="w-full bg-blue-900 text-white flex items-center px-10 py-4 shadow-md">
        <h1 className="text-2xl font-semibold">CareerConnect</h1>
      </header>

      <main className="flex-1 flex items-center justify-center px-4 py-10">
        <div className="bg-white rounded-xl shadow-xl w-full max-w-4xl p-8 relative">
          <button
            onClick={() => navigate(-1)}
            className="absolute right-6 top-4 bg-indigo-500 hover:bg-indigo-600 text-white text-xs px-4 py-1 rounded-full shadow"
          >
            Back
          </button>

          <h2 className="text-2xl font-semibold text-gray-900 mb-6">
            Create user account
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* Form */}
            <form onSubmit={handleSubmit} className="md:col-span-2 space-y-4">
              {[
                { label: "Name", name: "name", type: "text" },
                { label: "Gender", name: "gender", type: "text" },
                { label: "Email", name: "email", type: "email" },
                { label: "Mobile", name: "mobile", type: "text" },
                { label: "Password", name: "password", type: "password" },
                {
                  label: "Re-enter password",
                  name: "confirmPassword",
                  type: "password",
                },
                { label: "Student Type", name: "studentType", type: "text" },
                { label: "Department", name: "department", type: "text" },
              ].map((field) => (
                <div key={field.name}>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    {field.label}
                  </label>
                  <input
                    type={field.type}
                    name={field.name}
                    value={form[field.name]}
                    onChange={handleChange}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              ))}

              {message && (
                <p className="text-sm text-red-600 mt-2">{message}</p>
              )}

              <button
                type="submit"
                disabled={loading}
                className="mt-4 bg-indigo-600 hover:bg-indigo-700 disabled:opacity-60 text-white px-6 py-2 rounded-md shadow-md text-sm font-medium"
              >
                {loading ? "Creating..." : "Create Account"}
              </button>
            </form>

            {/* Image upload */}
            <div className="flex flex-col items-center justify-start space-y-4">
              <div className="w-32 h-32 rounded-md bg-gray-100 flex items-center justify-center shadow-inner overflow-hidden">
                {imagePreview ? (
                  <img
                    src={imagePreview}
                    alt="preview"
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <span className="text-gray-400 text-xs">Profile image</span>
                )}
              </div>

              <label className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-md text-sm font-medium shadow cursor-pointer">
                Upload Image
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="hidden"
                />
              </label>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default UserRegisterPage;
