import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const initialState = {
  title: "",
  category: "",
  department: "",
  studentCategory: "",
  gender: "Any",
  deadline: "",
  address: "",
  description: "",
  requirements: "",
  benefits: "",
  experience: "",
  salaryRange: "",
};

const AddJobPage = () => {
  const [formData, setFormData] = useState(initialState);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      await axios.post("/api/jobs", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      navigate("/company/posted-jobs");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Failed to create job");
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => navigate("/company/dashboard");

  return (
    <div className="flex min-h-screen bg-slate-100">
      {/* reuse sidebar later if you want, for now only form area */}
      <div className="flex-1 p-6">
        <form
          onSubmit={handleSubmit}
          className="bg-white rounded-md shadow p-6 space-y-4 max-w-4xl mx-auto"
        >
          <div className="flex justify-between items-center mb-2">
            <h2 className="text-lg font-semibold">Job Details</h2>
            <div className="space-x-2">
              <button
                type="button"
                onClick={handleCancel}
                className="bg-red-600 text-white px-4 py-1 rounded"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading}
                className="bg-green-600 text-white px-4 py-1 rounded disabled:opacity-60"
              >
                {loading ? "Posting..." : "Post"}
              </button>
            </div>
          </div>

          {/* top section */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-semibold mb-1">
                Job title
              </label>
              <input
                name="title"
                value={formData.title}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">
                Job Category
              </label>
              <input
                name="category"
                value={formData.category}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">
                Department
              </label>
              <input
                name="department"
                value={formData.department}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold mb-1">
                Student Category
              </label>
              <input
                name="studentCategory"
                value={formData.studentCategory}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">Gender</label>
              <input
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">
                Deadline
              </label>
              <input
                type="date"
                name="deadline"
                value={formData.deadline}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>

            <div className="md:col-span-3">
              <label className="block text-sm font-semibold mb-1">
                Address
              </label>
              <input
                name="address"
                value={formData.address}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
          </div>

          {/* long text areas */}
          <div className="space-y-3">
            <div>
              <label className="block text-sm font-semibold mb-1">
                Job Description
              </label>
              <textarea
                name="description"
                rows={3}
                value={formData.description}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">
                Job Requirements
              </label>
              <textarea
                name="requirements"
                rows={3}
                value={formData.requirements}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">
                Job Benefits
              </label>
              <textarea
                name="benefits"
                rows={2}
                value={formData.benefits}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">
                Work experience
              </label>
              <input
                name="experience"
                value={formData.experience}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">
                Salary Range
              </label>
              <input
                name="salaryRange"
                value={formData.salaryRange}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddJobPage;
