import React from "react";
import { useNavigate } from "react-router-dom";
import homeBg from "../images/home.jpg";

const HomePage = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col">
      {/* Top navbar */}
      <header className="w-full bg-blue-900 text-white flex justify-between items-center px-10 py-4 shadow-md">
        <h1 className="text-2xl font-semibold">CareerConnect</h1>
        <nav className="space-x-6 text-sm">
          <button className="hover:text-gray-300">Contact Us</button>
          <button className="hover:text-gray-300">About Us</button>
        </nav>
      </header>

      {/* Main content with background image */}
      <main
        className="flex-1 bg-cover bg-center bg-no-repeat"
        style={{ backgroundImage: `url(${homeBg})` }}
      >
        {/* Text block positioned like the second image */}
        <div className="h-full flex flex-col justify-start">
          <div className="mt-24 ml-24 max-w-xl">
            <h2 className="text-4xl md:text-5xl font-extrabold text-gray-900 mb-3">
              Welcome to <span className="text-blue-800">CareerConnect</span>
            </h2>

            <p className="text-lg text-gray-800 mb-8">
              A bridge between Company and BRAC University Students.
            </p>

            <div className="flex flex-wrap gap-4">
              <button
                onClick={() => navigate("/login")}
                className="bg-blue-700 hover:bg-blue-800 text-white px-8 py-2 rounded-md shadow-md text-sm font-semibold"
              >
                Login
              </button>

              <button
                onClick={() => navigate("/register-user")}
                className="bg-indigo-600 hover:bg-indigo-700 text-white px-8 py-2 rounded-md shadow-md text-sm font-semibold"
              >
                Register as User
              </button>

              <button
                onClick={() => navigate("/register-company")}
                className="bg-purple-600 hover:bg-purple-700 text-white px-8 py-2 rounded-md shadow-md text-sm font-semibold"
              >
                Register as Company
              </button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
