// src/pages/PostedJobsPage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const PostedJobsPage = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const fetchJobs = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/jobs/company", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobs(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert(
        err.response?.data?.error ||
          err.response?.data?.message ||
          "Failed to load jobs"
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this job?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/jobs/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobs((prev) => prev.filter((job) => job._id !== id));
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert(
        err.response?.data?.error ||
          err.response?.data?.message ||
          "Failed to delete job"
      );
    }
  };

  return (
    <div className="min-h-screen bg-slate-900 flex flex-col">
      {/* top bar */}
      <header className="w-full flex items-center justify-between px-8 py-3 bg-slate-900 text-white">
        <h1 className="text-2xl font-semibold">CareerConnect</h1>

        <div className="flex items-center gap-4">
          <div className="flex items-center bg-white rounded-full px-3 py-1">
            <span className="text-gray-500 mr-2">üîç</span>
            <input
              type="text"
              placeholder="Search"
              className="bg-transparent outline-none text-sm text-gray-700"
            />
          </div>
          <button
            className="text-2xl font-bold"
            onClick={() => navigate("/company/dashboard")}
          >
            ‚ò∞
          </button>
        </div>
      </header>

      {/* main area with sidebar + content */}
      <div className="flex flex-1">
        {/* left sidebar */}
        <aside className="w-64 bg-slate-900 text-white pt-6 hidden md:block">
          <nav className="space-y-2 px-4">
            <button
              className="w-full text-left px-3 py-2 rounded hover:bg-slate-800"
              onClick={() => navigate("/company/dashboard")}
            >
              Dashboard
            </button>
            <button
              className="w-full text-left px-3 py-2 rounded bg-slate-800"
              onClick={() => navigate("/company/posted-jobs")}
            >
              Posted Jobs
            </button>
            <button
              className="w-full text-left px-3 py-2 rounded hover:bg-slate-800"
              onClick={() => navigate("/company/candidates")}
            >
              Candidate list
            </button>
            <button
              className="w-full text-left px-3 py-2 rounded hover:bg-slate-800"
              onClick={() => navigate("/company/messages")}
            >
              Messages
            </button>
            <button
              className="w-full text-left px-3 py-2 rounded hover:bg-slate-800"
              onClick={() => navigate("/company/profile")}
            >
              Profile
            </button>
          </nav>
        </aside>

        {/* content pane */}
        <main className="flex-1 bg-gradient-to-b from-slate-200 to-slate-400 py-10 flex justify-center">
          <div className="w-full max-w-5xl mx-4">
            <div className="bg-white rounded-xl shadow-2xl p-6 md:p-8">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-semibold text-slate-900">
                  Posted Jobs
                </h2>
                <button
                  className="bg-[#2e7d32] hover:bg-[#1b5e20] text-white px-5 py-2 rounded shadow text-sm font-semibold"
                  onClick={() => navigate("/company/jobs/new")}
                >
                  + New Job
                </button>
              </div>

              {loading ? (
                <p>Loading...</p>
              ) : jobs.length === 0 ? (
                <p>No jobs posted yet.</p>
              ) : (
                <div className="space-y-6">
                  {jobs.map((job) => (
                    <div
                      key={job._id}
                      className="border border-slate-200 rounded-lg p-4 md:p-5 flex justify-between items-start gap-4"
                    >
                      <div className="text-sm text-slate-900 leading-relaxed">
                        <p>
                          <span className="font-semibold text-pink-700">
                            Job Title:
                          </span>{" "}
                          {job.title}
                        </p>
                        <p>
                          <span className="font-semibold text-pink-700">
                            Job Category:
                          </span>{" "}
                          {job.category}
                        </p>
                        <p>
                          <span className="font-semibold text-pink-700">
                            Department:
                          </span>{" "}
                          {job.department}
                        </p>
                        <p>
                          <span className="font-semibold text-pink-700">
                            Student Category:
                          </span>{" "}
                          {job.studentCategory}
                          {"  "}
                          <span className="font-semibold text-pink-700 ml-4">
                            Gender:
                          </span>{" "}
                          {job.gender}
                        </p>
                        <p>
                          <span className="font-semibold text-pink-700">
                            Deadline:
                          </span>{" "}
                          {new Date(job.deadline).toLocaleDateString()}
                        </p>
                        <p>
                          <span className="font-semibold text-pink-700">
                            Address:
                          </span>{" "}
                          {job.address}
                        </p>

                        <p className="font-semibold text-pink-700 mt-3">
                          Job Description
                        </p>
                        <p className="whitespace-pre-line text-gray-700">
                          {job.description}
                        </p>
                      </div>

                      <div className="space-y-2">
                        <button
                          className="bg-green-600 hover:bg-green-700 text-white px-5 py-1.5 rounded shadow text-sm"
                          onClick={() =>
                            navigate(`/company/jobs/${job._id}/edit`)
                          }
                        >
                          Edit
                        </button>
                        <button
                          className="bg-red-600 hover:bg-red-700 text-white px-5 py-1.5 rounded shadow text-sm"
                          onClick={() => handleDelete(job._id)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default PostedJobsPage;
