import React from "react";
import { useNavigate } from "react-router-dom";

const CompanyRegisterPage = () => {
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // later: send data to backend /api/auth/register-company
    console.log("Company register submit");
  };

  return (
    <div className="min-h-screen flex flex-col bg-sky-100">
      {/* Top bar */}
      <header className="w-full bg-blue-900 text-white flex items-center px-10 py-4 shadow-md">
        <h1 className="text-2xl font-semibold">CareerConnect</h1>
      </header>

      {/* Card */}
      <main className="flex-1 flex items-center justify-center px-4 py-10">
        <div className="bg-white rounded-xl shadow-xl w-full max-w-4xl p-8 relative">
          <button
            onClick={() => navigate(-1)}
            className="absolute right-6 top-4 bg-indigo-500 hover:bg-indigo-600 text-white text-xs px-4 py-1 rounded-full shadow"
          >
            Back
          </button>

          <h2 className="text-2xl font-semibold text-gray-900 mb-6">
            Create company account
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* Form fields */}
            <form
              onSubmit={handleSubmit}
              className="md:col-span-2 space-y-4"
            >
              {[
                { label: "Company Name", type: "text" },
                { label: "Establishment Year", type: "number" },
                { label: "Contact No", type: "text" },
                { label: "Email", type: "email" },
                { label: "Password", type: "password" },
                { label: "Re-enter password", type: "password" },
                { label: "Industry Type", type: "text" },
                { label: "Address", type: "text" },
                { label: "License No", type: "text" },
              ].map((field) => (
                <div key={field.label}>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    {field.label}
                  </label>
                  <input
                    type={field.type}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              ))}

              <button
                type="submit"
                className="mt-4 bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-2 rounded-md shadow-md text-sm font-medium"
              >
                Create Account
              </button>
            </form>

            {/* Logo upload section */}
            <div className="flex flex-col items-center justify-start space-y-4">
              <div className="w-32 h-32 rounded-md bg-gray-100 flex items-center justify-center shadow-inner">
                <span className="text-gray-400 text-xs">Company logo</span>
              </div>
              <button
                type="button"
                className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-md text-sm font-medium shadow"
              >
                Upload Image
              </button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default CompanyRegisterPage;
